import{u as _}from"./chunks/theme.0a2bfe2c.js";import{d as k,b as g,o as i,c as p,C as n,f as b,t as r,G as a,F as h,R as d,V as q,D as y}from"./chunks/framework.52ec5009.js";const w={class:"demo"},x={key:0,style:{fontSize:"20px",fontWeight:"bold"}},v={key:1,style:{fontSize:"14px"}},S={key:2},C={style:{color:"#ccc",fontSize:"12px"}},A=k({__name:"PoemsContainer",props:{title:{default:""},author:{default:""},comment:{default:""},content:{default:()=>[]}},setup(s){const{title:e,author:t,content:l}=s,o=g(`${e} ${t} ${l.join("")}`),{copy:m}=_({source:o});return(f,c)=>(i(),p("div",w,[n("span",{class:"demo-copy",onClick:c[0]||(c[0]=u=>b(m)(o.value))},"copy"),e?(i(),p("p",x," 《"+r(e)+"》 ")):a("",!0),t?(i(),p("p",v,r(t))):a("",!0),l.length?(i(),p("div",S,[(i(),p(h,null,d(l,u=>n("p",null,r(u),1)),64))])):a("",!0),n("p",C,r(f.comment),1)]))}}),P=[{title:"唐多令·芦叶满汀洲",author:"宋·刘过",content:["芦叶满汀洲，寒沙带浅流。二十年重过南楼。","柳下系船犹未稳，能几日，又中秋。","黄鹤断矶头，故人曾到否？旧江山浑是新愁。","欲买桂花同载酒，终不似，少年游。"]},{title:"谒金门·风乍起",author:"五代·冯延巳",content:["风乍起，吹皱一池春水。闲引鸳鸯香径里，手挼红杏蕊。","斗鸭阑干独倚，碧玉搔头斜坠。终日望君君不至，举头闻鹊喜。"]},{title:"一剪梅",author:"明·唐寅",content:["雨打梨花深闭门，忘了青春，误了青春。赏心乐事共谁论？花下销魂，月下销魂。","愁聚眉峰尽日颦，千点啼痕，万点啼痕。晓看天色暮看云，行也思君，坐也思君。"]},{title:"雨霖铃·寒蝉凄切",author:"宋·柳永",content:["寒蝉凄切，对长亭晚，骤雨初歇。都门帐饮无绪，留恋处，兰舟催发。","执手相看泪眼，竟无语凝噎。念去去，千里烟波，暮霭沉沉楚天阔。","多情自古伤离别，更那堪，冷落清秋节！今宵酒醒何处？杨柳岸，晓风残月。","此去经年，应是良辰好景虚设。便纵有千种风情，更与何人说？"]},{title:"虞美人·寄公度",author:"宋·舒亶",content:["芙蓉落尽天涵水，日暮沧波起。背飞双燕贴云寒，独向小楼东畔、倚阑看。","浮生只合尊前老，雪满长安道。故人早晚上高台，赠我江南春色、一枝梅。"]},{title:"峨眉山月歌",author:"唐·李白",content:["峨眉山月半轮秋，影入平羌江水流。","夜发清溪向三峡，思君不见下渝州。"]},{title:"一剪梅·红藕香残玉簟秋",author:"宋·李清照",content:["红藕香残玉簟秋。轻解罗裳，独上兰舟。云中谁寄锦书来？雁字回时，月满西楼。","花自飘零水自流。一种相思，两处闲愁。此情无计可消除，才下眉头，却上心头。"]},{title:"鹧鸪天·桂花",author:"宋·李清照",content:["暗淡轻黄体性柔，情疏迹远只香留。何须浅碧深红色，自是花中第一流。","梅定妒，菊应羞，画阑开处冠中秋。骚人可煞无情思，何事当年不见收。"],comment:"阑 通：栏"},{title:"清平乐·年年雪里",author:"宋·李清照",content:["年年雪里，常插梅花醉。挼尽梅花无好意，赢得满衣清泪。","今年海角天涯，萧萧两鬓生华。看取晚来风势，故应难看梅花。"]},{title:"武陵春·春晚",author:"宋·李清照",content:["风住尘香花已尽，日晚倦梳头。物是人非事事休，欲语泪先流。","闻说双溪春尚好，也拟泛轻舟。只恐双溪舴艋舟，载不动许多愁。"]},{title:"行香子·七夕",author:"宋·李清照",content:["草际鸣蛩。惊落梧桐。正人间、天上愁浓。云阶月地，关锁千重。纵浮槎来，浮槎去，不相逢。","星桥鹊驾，经年才见，想离情、别恨难穷。牵牛织女，莫是离中。甚霎儿晴，霎儿雨，霎儿风。"]}],T=q("",7),W=JSON.parse('{"title":"Excerpt","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"notes/read/excerpt.md"}'),D={name:"notes/read/excerpt.md"},$=Object.assign(D,{setup(s){return(e,t)=>(i(),p("div",null,[T,(i(!0),p(h,null,d(b(P),(l,o)=>(i(),y(A,{key:o,title:l.title,author:l.author,content:l.content,comment:l.comment},null,8,["title","author","content","comment"]))),128))]))}});export{W as __pageData,$ as default};
